import data from './assets/blog-db.json'
import { useState } from 'react';
export default function BlogPage() {
    let url = window.location.href;
    const foundId = url.split('id=')[1];
    const { title, article } = data.blogEntries.find((entry) => entry.id === foundId);
    const [val, setVal] = useState('');
    const handleChange = (event) => {
        setVal(event.target.value)
        event.preventDefault()
    }
    if (data)
        return (
            <article className='container'>
                <input value={val} onChange={handleChange} />
                <div className='row text-center'>
                    < img className='img-fluid' src={"/landscape.png"} alt='Post' />
                </div>
                <div className='row'>
                    <p className='card-title'>April 9 2025</p>
                    <h3>{title}</h3>
                    {article?.map(sect => {
                        return <p>{sect}</p>
                    })}
                    <p></p>
                </div>
            </article >
        )
}
